const FILEREGEX = /^(local|keystone-cloud):file:([^\\\/:\n]+)/;
const getFileRef = (mode, name) => `${mode}:file:${name}`;
const parseFileRef = ref => {
  const match = ref.match(FILEREGEX);

  if (match) {
    const [, mode, filename] = match;
    return {
      mode: mode,
      filename: filename
    };
  }

  return undefined;
};

export { getFileRef, parseFileRef };
